mapper.update:
UPDATE blog_blog
title = #{title}, content = #{content}, preview = #{preview}, state = #{state}, remark = #{remark}, abs = #{abs}, pv = #{pv}, create_id = #{createId}, gmt_create = #{gmtCreate}, gmt_modified = #{gmtModified},
WHERE id = #{id}

mapper.select:
SELECT
    count(0)
FROM
blog_blog

     and id != #{noid}
     and title = #{title}
