mapper.update:
UPDATE zhousb_blog
title = #{title}, type = #{type}, preview = #{preview}, remark = #{remark}, state = #{state}, content = #{content}, gmt_modified = NOW()
WHERE id = #{id}

mapper.select:
SELECT zhousb_blog.id, zhousb_blog.title, zhousb_blog.type, zhousb_blog_type.`name` typeName, zhousb_blog.remark, zhousb_blog.pv, zhousb_blog.gmt_create FROM zhousb_blog LEFT JOIN zhousb_blog_type ON zhousb_blog.type = zhousb_blog_type.`key`
zhousb_blog.state=0 and zhousb_blog.title LIKE CONCAT('%',#{titleLike},'%') and zhousb_blog.type = #{type}
ORDER BY id DESC
