mapper.update:
UPDATE zhousb_blog_type
name = #{name}, title = #{title}, `desc` = #{desc}, bg = #{bg}, remark = #{remark}, state = #{state}, gmt_modified = NOW()
WHERE id = #{id}

mapper.select:
SELECT
    count(0)
FROM
zhousb_blog_type

    state != 11
    and id != #{noid}
     and `key` = #{key}
     and name = #{name}
