mapper.update:
update system_menu
parent_id = #{parentId}, name = #{name}, icon = #{icon}, path = #{path}, type = #{type}, state = #{state}, sort = #{sort}, `key` = #{key}, modified_id = #{modifiedId}, gmt_modified = NOW()
where id = #{id} and flag = #{flag}

mapper.select:
SELECT
COUNT( 0 )
FROM
system_menu

    flag = #{flag}
    and id != #{exclId}
    and parent_id = #{parentId}
    and name = #{name}
    and `key` = #{key}
